import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router';
import { useMobileDetect } from '../../hooks';
import GoogleLogin, { GoogleLoginResponse } from 'react-google-login';

import { loginGoogle } from '../../features/authSlice';
//@ts-ignore
import { getListCumpus } from '../../features/cumpusSlice/cumpusSlice';
//@ts-ignore
import { defaultTime } from '../../features/semesters/semestersSlice';

import styles from './Login.module.css';
import { Select, message, Image, Space, Layout, Row, Col, Button } from 'antd';
import { FcGoogle } from 'react-icons/fc';
const { Option } = Select;
const { Content } = Layout;

// TODO: Remove @ts-ignore

const Login = () => {
	const clientID = import.meta.env.VITE_GOOGLE_CLIENT_ID;
	const dispatch = useDispatch();
	const navigate = useNavigate();
	const [cumpus, setCumpus] = useState('');
	//@ts-ignore
	const { listCumpus } = useSelector((state) => state.cumpus); // = [{ _id: '63eef716c9ef6d077f1f1deb', name: 'Hanoi' }];
	const isMobile = useMobileDetect();

	// Login Reject
	const handleFailure = () => {
		message.error('500: Internal Server Error');
	};

	const handleLogin = ({ tokenId }: GoogleLoginResponse) => {
		dispatch(
			defaultTime({
				filter: { campus_id: cumpus },
				callback: (res: DataResponse) => {
					if (!res.success) return;
					const dataForm = {
						token: tokenId,
						cumpusId: cumpus,
						smester_id: res?.result?._id,
					};

					// useLoginMutation({ val: dataForm, callback: loginCallback });
					//@ts-ignore
					dispatch(loginGoogle({ val: dataForm, callback: loginCallback }));
				},
			})
		);
	};

	// Redirect user on successful login
	const loginCallback = (payload: any) => {
		const { isAdmin, manager } = payload;

		if (!payload?.success) return message.error('Đăng nhập thất bại');

		message.success('Đăng nhập thành công');

		return isAdmin
			? manager.role === 2
				? navigate('/employee-manager')
				: navigate('/status')
			: navigate('/info-student');
	};

	const handleChange = (value: string) => {
		setCumpus(value);
	};

	useEffect(() => {
		dispatch(getListCumpus());
	}, [dispatch]);

	return (
		<Layout>
			<Row justify="center" align="middle" style={{ minHeight: '100vh', overflow: 'hidden' }}>
				<Col span={isMobile ? 18 : 8}>
					<Content>
						<Space className={styles.campus} size={42} direction="vertical">
							<Image
								preview={false}
								alt="FPT_LOGO"
								className={styles.image_logo}
								src="https://career.fpt.edu.vn/Content/images/logo_unit/Poly.png"
								fallback="data:image/png;base64,"
							></Image>
							<Select
								size="large"
								className={styles.image_logo}
								defaultValue="Lựa chọn cơ sở"
								onChange={handleChange}
							>
								{listCumpus &&
									listCumpus.map((item: { _id: string; name: string }) => (
										<Option key={item._id} value={item._id}>
											{item.name}
										</Option>
									))}
							</Select>
							<GoogleLogin
								clientId={clientID}
								//@ts-ignore
								onSuccess={handleLogin}
								onFailure={handleFailure}
								render={(renderProps) => (
									<Button
										type="primary"
										className={styles.button_login}
										icon={<FcGoogle />}
										onClick={renderProps.onClick}
										disabled={!cumpus}
									>
										Sign in with Google
									</Button>
								)}
							/>
						</Space>
					</Content>
				</Col>
			</Row>
		</Layout>
	);
};

export default Login;
